import {createContext, useState } from "react";

const ThemeContext = createContext()

const ThemeProvider = ({children}) => {
  
  const defaultTheme = 'theme-1'
  const [theme, setTheme] = useState(defaultTheme)
  
  const multipleThemes = {
    "theme-1": '1',
    "theme-2": '2',
    "theme-3": '3'
  }

  setTheme = multipleThemes[theme] || defaultTheme

  // const handleTheme = (changeTheme) => {
  //   if(changeTheme === '1') {
  //     setTheme('1')
  //     console.log('entre a theme 1')
  //   }

  //   else if(changeTheme === '2') {
  //     setTheme('2')
  //     console.log('entre a theme 2')

  //   }

  //   else if(changeTheme === '3') {
  //     setTheme('3')
  //     console.log('entre a theme 3')
  //   }
  // }

  const data = { theme, multipleThemes }


  return (
    <ThemeContext.Provider value={data}>{children}</ThemeContext.Provider>
  )
}

export { ThemeProvider }

export default ThemeContext;
